import "./App.css";
import {
  FormSection,
  Formular,
  InputDiv,
  MainTitle,
  PageContainer,
  SectionTitle,
  KontrolaButton,
  Text
} from "./AppStyles";
//
import { useReducer, useState, useEffect } from "react";
// initialState pro useReducer
const defaultObjednavka = {
  typ: "",
  hmotnost: 0,
  doprava: 0,
  bioKvalita: false,
  premiumKvalita: false,
  chudiKvalita: false,
  darkoveBaleni: false,
  rozpocet: 0,
  email: "",
};
// reducer funkce pro useReducer
function setObjednavka(objednavka, action) {
  switch (action.type) {
    case "update_text":
      return { ...objednavka, [action.key]: action.value };
    case "update_number":
      return { ...objednavka, [action.key]: parseFloat(action.value) };
    case "toggle_bio":
      return { ...objednavka, bioKvalita: !objednavka.bioKvalita };
    case "toggle_premium":
      return { ...objednavka, premiumKvalita: !objednavka.premiumKvalita };
    case "toggle_chudi":
      return { ...objednavka, chudiKvalita: !objednavka.chudiKvalita };
    case "toggle_darek":
      return { ...objednavka, darkoveBaleni: !objednavka.darkoveBaleni };
    default:
      return objednavka;
  }
}

function App() {
  const [finalPrice, setFinalPrice] = useState(0);
  const [showFinalPrice, setShowFinalPrice] = useState(0);
  const [checked, setChecked] = useState();
  const [gift, setGift] = useState();
  //state objednavky spravujeme pomoci useReducer hooku
  const [objednavka, dispatch] = useReducer(setObjednavka, defaultObjednavka);

  // useEffect(() => { console.log(JSON.stringify(objednavka)) }, [objednavka]);
  useEffect(() => {
    // console.log(JSON.stringify(objednavka));
    let newFinalPrice = getFinalPrice(objednavka);
    setShowFinalPrice(newFinalPrice);
    gifted(showFinalPrice);
  }, [objednavka]);

  const getFinalPrice = (objednavka) => {
    let thisBasePrice = objednavka.typ * objednavka.hmotnost;
    let thisFinalPrice = thisBasePrice;
    if (objednavka.doprava === 250) {
      thisFinalPrice += 250;
    } else if (objednavka.doprava === 0.1) {
      thisFinalPrice += thisBasePrice * 0.1;
    }
    if (objednavka.bioKvalita) {
      thisFinalPrice += thisBasePrice * 0.3;
    }
    if (objednavka.premiumKvalita) {
      thisFinalPrice += thisBasePrice * 0.5;
    }
    if (objednavka.chudiKvalita) {
      thisFinalPrice -= thisBasePrice * 0.15;
    }
    if (objednavka.darkoveBaleni) {
      thisFinalPrice += 500;
    }
    setFinalPrice(thisFinalPrice);
    return thisFinalPrice;
  };

  const checkPrice = (objednavka) => {
    if (objednavka.rozpocet >= finalPrice) {
      let checkOk = 1;
      setChecked(checkOk);
    } else {
      let checkNotOk = 2;
      setChecked(checkNotOk);
    }
  };

  const gifted = (showFinalPrice) => {
    if (showFinalPrice >= 2000) {
      let giftOk = 1;
      setGift (giftOk);
    } else if (showFinalPrice < 2000) {
      let giftNotOk = 2;
      setGift (giftNotOk);
    }
  };

  return (
    <PageContainer>
      <Formular>
        <FormSection name="nadpis">
          <MainTitle>Vaše objednávka</MainTitle>
        </FormSection>
        <FormSection name="vyber">
          <SectionTitle>Destinace</SectionTitle>
          {/* VYBER TYPU KRMIVA */}
          <label>Výběr destinace:</label>
          <select
            id="typ"
            onChange={(e) => {
              dispatch({
                type: "update_number",
                value: e.target.value,
                key: "typ",
              });
            }}
          >
            <option value={0}>nevybráno</option>
            <option value={500}>Praha 500 kč</option>
            <option value={3000}>Frankfurt 3 000 kč</option>
            <option value={15000}>New York 15 000 kč</option>
            <option value={30000}>Sydney 30 000 kč</option>
          </select>
          {/* HMOTNOST KRMIVA */}
          <label>Pocet letenek (1-10):</label>
          <select
            id="typ"
            onChange={(e) => {
              dispatch({
                type: "update_number",
                value: e.target.value,
                key: "typ",
              });
            }}
          >
            <option value={0}>nevybráno</option>
            <option value={1}>1</option>
            <option value={2}>2</option>
            <option value={3}>3</option>
            <option value={4}>4</option>
            <option value={5}>5</option>
            <option value={6}>6</option>
            <option value={7}>7</option>
            <option value={8}>8</option>
            <option value={9}>9</option>
            <option value={10}>10</option>
          </select>

        </FormSection>
        {/* VLASTNOSTI / EXTRAS */}
        <FormSection name="vlastnosti">
          <SectionTitle>Zpáteční letenka</SectionTitle>
          <InputDiv>
            <input
              type="checkbox"
              id="bioKvalita"
              onChange={(e) => {
                dispatch({
                  type: "toggle_bio",
                });
              }}
            />
            <label>Pokud chcete zpáteční letenku zaškrtněte pole.</label>
          </InputDiv>

        </FormSection>
        <FormSection name="doprava">
          {/* DOPRAVA */}
          <SectionTitle>Třída</SectionTitle>
          <div>
            <input
              type="radio"
              name="doprava"
              id="dopravaOdber"
              value={0}
              onChange={(e) => {
                dispatch({
                  type: "update_number",
                  value: e.target.value,
                  key: "doprava",
                });
              }}
            />
            <label>Economy class - bez přirážky</label>
            <input
              type="radio"
              name="doprava"
              id="dopravaFiremniKuryr"
              value={0.1}
              onChange={(e) => {
                dispatch({
                  type: "update_number",
                  value: e.target.value,
                  key: "doprava",
                });
              }}
            />
            <label>Business class - +25%</label>
            <input
              type="radio"
              name="doprava"
              id="dopravaFiremniKuryr"
              value={0.1}
              onChange={(e) => {
                dispatch({
                  type: "update_number",
                  value: e.target.value,
                  key: "doprava",
                });
              }}
            />
            <label>Royal class - +50%</label>

          </div>
        </FormSection>
        <FormSection name="kalkulace">
          {/* FINALNI CENA - ZOBRAZENI + ROZPOCET */}
          <SectionTitle>Konečná kalkulace</SectionTitle>
          <label>Zadejte váš rozpočet:</label>
          <input
            type="text"
            id="rozpocet"
            value={objednavka.rozpocet}
            onChange={(e) => {
              dispatch({
                type: "update_number",
                value: e.target.value,
                key: "rozpocet",
              });
            }}
          />
          <label>Finální cena:</label>
          <input type="text" id="finalniCena" value={showFinalPrice} disabled />
          <KontrolaButton
            checkedPrice={checked}
            onClick={() => {
              checkPrice(objednavka);
            }}
          >
            Kontrola
          </KontrolaButton>
          <Text 
          gifted={gift}
          >Nakoupili jste za více než 2000, a proto k objednávce obdržíte dárek.
          </Text>
        </FormSection>
        <FormSection name="email">
          <SectionTitle>Kontaktní údaje</SectionTitle>
          <label>Zadejte vaši emailovou adresu</label>
          <input
            type="text"
            id="email"
            value={objednavka.email}
            onChange={(e) => {
              dispatch({
                type: "update_text",
                value: e.target.value,
                key: "email",
              });
            }}
          />
        </FormSection>
      </Formular>
    </PageContainer>
  );
}
export default App;
