import "./App.css";
import {
  FormSection,
  Formular,
  InputDiv,
  MainTitle,
  PageContainer,
  SectionTitle,
  KontrolaButton,
  Text
} from "./AppStyles";
//
import { useReducer, useState, useEffect } from "react";
// initialState pro useReducer
const defaultObjednavka = {
  typ: "",
  hmotnost: 0,
  doprava: 0,
  bioKvalita: false,
  premiumKvalita: false,
  chudiKvalita: false,
  darkoveBaleni: false,
  rozpocet: 0,
  email: "",
};
// reducer funkce pro useReducer
function setObjednavka(objednavka, action) {
  switch (action.type) {
    case "update_text":
      return { ...objednavka, [action.key]: action.value };
    case "update_number":
      return { ...objednavka, [action.key]: parseFloat(action.value) };
    case "toggle_bio":
      return { ...objednavka, bioKvalita: !objednavka.bioKvalita };
    case "toggle_premium":
      return { ...objednavka, premiumKvalita: !objednavka.premiumKvalita };
    case "toggle_chudi":
      return { ...objednavka, chudiKvalita: !objednavka.chudiKvalita };
    case "toggle_darek":
      return { ...objednavka, darkoveBaleni: !objednavka.darkoveBaleni };
    default:
      return objednavka;
  }
}

function App() {
  const [finalPrice, setFinalPrice] = useState(0);
  const [showFinalPrice, setShowFinalPrice] = useState(0);
  const [checked, setChecked] = useState();
  const [gift, setGift] = useState();
  //state objednavky spravujeme pomoci useReducer hooku
  const [objednavka, dispatch] = useReducer(setObjednavka, defaultObjednavka);

  // useEffect(() => { console.log(JSON.stringify(objednavka)) }, [objednavka]);
  useEffect(() => {
    // console.log(JSON.stringify(objednavka));
    let newFinalPrice = getFinalPrice(objednavka);
    setShowFinalPrice(newFinalPrice);
  }, [objednavka]);

  const getFinalPrice = (objednavka) => {
    let thisBasePrice = objednavka.typ * objednavka.hmotnost;
    let thisFinalPrice = thisBasePrice;
    if (objednavka.doprava === 250) {
      thisFinalPrice += 250;
    } else if (objednavka.doprava === 0.1) {
      thisFinalPrice += thisBasePrice * 0.1;
    }
    if (objednavka.bioKvalita) {
      thisFinalPrice += thisBasePrice * 0.3;
    }
    if (objednavka.premiumKvalita) {
      thisFinalPrice += thisBasePrice * 0.5;
    }
    if (objednavka.chudiKvalita) {
      thisFinalPrice -= thisBasePrice * 0.15;
    }
    if (objednavka.darkoveBaleni) {
      thisFinalPrice += 500;
    }
    setFinalPrice(thisFinalPrice);
    return thisFinalPrice;
  };

  const checkPrice = (objednavka) => {
    if (objednavka.rozpocet >= finalPrice) {
      let checkOk = 1;
      setChecked(checkOk);
    } else {
      let checkNotOk = 2;
      setChecked(checkNotOk);
    }
  };

  const gifted = (finalPrice) => {
    if (finalPrice >= 2000) {
      let giftOk = 1;
      setGift (giftOk);
      console.log(gift)
    } else if (finalPrice < 2000) {
      let giftNotOk = 2;
      setGift (giftNotOk);
    }
  };

  return (
    <PageContainer>
      <Formular>
        <FormSection name="nadpis">
          <MainTitle>Vaše objednávka</MainTitle>
        </FormSection>
        <FormSection name="vyber">
          <SectionTitle>Výběr krmiva</SectionTitle>
          {/* VYBER TYPU KRMIVA */}
          <label>Výběr typu krmiva:</label>
          <select
            id="typ"
            onClick={(e) => {
              dispatch({
                type: "update_number",
                value: e.target.value,
                key: "typ",
              });
            }}
          >
            <option value={0}>nevybráno</option>
            <option value={150}>psí žrádlo 150kč/kg</option>
            <option value={120}>kočičí žrádlo 120kč/kg</option>
            <option value={50}>žrádlo pro rybičky 50kč/kg</option>
            <option value={800}>žrádlo pro tygra 800kč/kg</option>
          </select>
          {/* HMOTNOST KRMIVA */}
          <label>Hmotnost krmiva (kg):</label>
          <input
            type="text"
            id="hmotnost"
            value={objednavka.hmotnost}
            onChange={(e) => {
              dispatch({
                type: "update_number",
                value: e.target.value,
                key: "hmotnost",
              });
            }}
          />
        </FormSection>
        {/* VLASTNOSTI / EXTRAS */}
        <FormSection name="vlastnosti">
          <SectionTitle>Vlastnosti krmiva</SectionTitle>
          <InputDiv>
            <input
              type="checkbox"
              id="bioKvalita"
              onChange={(e) => {
                dispatch({
                  type: "toggle_bio",
                });
              }}
            />
            <label>Bio kvalita (+30%)</label>
          </InputDiv>
          <InputDiv>
            <input
              type="checkbox"
              id="premiumKvalita"
              onChange={(e) => {
                dispatch({
                  type: "toggle_premium",
                });
              }}
            />
            <label>Extra prémium kvalita (+50%)</label>
          </InputDiv>
          <InputDiv>
            <input
              type="checkbox"
              id="chudaKvalita"
              onChange={(e) => {
                dispatch({
                  type: "toggle_chudi",
                });
              }}
            />
            <label>Extra nekvalitní pro chudé (-15%)</label>
          </InputDiv>
          <InputDiv>
            <input
              type="checkbox"
              id="darkoveBaleni"
              onChange={() => {
                dispatch({
                  type: "toggle_darek",
                });
              }}
            />
            <label>Dárkové balení (+500kč)</label>
          </InputDiv>
        </FormSection>
        <FormSection name="doprava">
          {/* DOPRAVA */}
          <SectionTitle>Doprava</SectionTitle>
          <div>
            <input
              type="radio"
              name="doprava"
              id="dopravaOdber"
              value={0}
              onChange={(e) => {
                dispatch({
                  type: "update_number",
                  value: e.target.value,
                  key: "doprava",
                });
              }}
            />
            <label>osobní odběr (zdarma)</label>
            <input
              type="radio"
              name="doprava"
              id="dopravaFiremniKuryr"
              value={0.1}
              onChange={(e) => {
                dispatch({
                  type: "update_number",
                  value: e.target.value,
                  key: "doprava",
                });
              }}
            />
            <label>firemní kurýr (+10%)</label>
            <input
              type="radio"
              name="doprava"
              id="dopravaCeskaPosta"
              value={250}
              onChange={(e) => {
                dispatch({
                  type: "update_number",
                  value: e.target.value,
                  key: "doprava",
                });
              }}
            />
            <label>Česká Pošta (+250kč)</label>
          </div>
        </FormSection>
        <FormSection name="kalkulace">
          {/* FINALNI CENA - ZOBRAZENI + ROZPOCET */}
          <SectionTitle>Konečná kalkulace</SectionTitle>
          <label>Zadejte váš rozpočet:</label>
          <input
            type="text"
            id="rozpocet"
            value={objednavka.rozpocet}
            onChange={(e) => {
              dispatch({
                type: "update_number",
                value: e.target.value,
                key: "rozpocet",
              });
            }}
          />
          <label>Finální cena:</label>
          <input type="text" id="finalniCena" value={showFinalPrice} disabled />
          <KontrolaButton
            checkedPrice={checked}
            onClick={() => {
              checkPrice(objednavka);
            }}
          >
            Kontrola
          </KontrolaButton>
          <Text 
          gifted={gift}
          onChange={()=> {
            gifted(objednavka);
          }} >Nakoupili jste za více než 2000, a proto k objednávce obdržíte dárek.
          </Text>
        </FormSection>
        <FormSection name="email">
          <SectionTitle>Kontaktní údaje</SectionTitle>
          <label>Zadejte vaši emailovou adresu</label>
          <input
            type="text"
            id="email"
            value={objednavka.email}
            onChange={(e) => {
              dispatch({
                type: "update_text",
                value: e.target.value,
                key: "email",
              });
            }}
          />
        </FormSection>
      </Formular>
    </PageContainer>
  );
}
export default App;
