import "./App.css";
import {
  FormSection,
  Formular,
  InputDiv,
  MainTitle,
  PageContainer,
  SectionTitle,
  KontrolaButton,
  Text,
  Potvrdit,
} from "./AppStyles";
//
import { useReducer, useState, useEffect } from "react";
// initialState pro useReducer
const defaultObjednavka = {
  zapujcka: 0,
  doplnky: 0,
  horske: false,
  pocHor: 1,
  detske: false,
  pocDet: 1,
  silnicni: false,
  pocSil: 1,
  gravel: false,
  pocGra: 1,
  rozpocet: 0,
};
// reducer funkce pro useReducer
function setObjednavka(objednavka, action) {
  switch (action.type) {
    case "update_text":
      return { ...objednavka, [action.key]: action.value };
    case "update_number":
      return { ...objednavka, [action.key]: parseFloat(action.value) };
    case "toggle_horske":
      return { ...objednavka, horske: !objednavka.horske };
    case "toggle_detske":
      return { ...objednavka, detske: !objednavka.detske };
    case "toggle_silnicni":
      return { ...objednavka, silnicni: !objednavka.silnicni };
    case "toggle_gravel":
      return { ...objednavka, gravel: !objednavka.gravel };
    default:
      return objednavka;
  }
}

function App() {
  const [finalPrice, setFinalPrice] = useState(0);
  const [showFinalPrice, setShowFinalPrice] = useState(0);
  const [checked, setChecked] = useState();
  const [gift, setGift] = useState();
  //state objednavky spravujeme pomoci useReducer hooku
  const [objednavka, dispatch] = useReducer(setObjednavka, defaultObjednavka);

  const handleSubmit = (event) => {
    event.preventDefault();
  }

  // useEffect
  useEffect(() => {
    // console.log(JSON.stringify(objednavka));
    let newFinalPrice = getFinalPrice(objednavka);
    setShowFinalPrice(newFinalPrice);
    gifted(showFinalPrice);
  }, [objednavka]);

  const getFinalPrice = (objednavka) => {
    let thisBasePrice = 0;
    let thisFinalPrice = thisBasePrice;

    if (objednavka.horske) {
      thisFinalPrice += objednavka.pocHor * 500;
    }
    if (objednavka.detske) {
      thisFinalPrice += objednavka.pocDet * 200;
    }
    if (objednavka.silnicni) {
      thisFinalPrice += objednavka.pocSil * 1500;
    }
    if (objednavka.gravel) {
      thisFinalPrice += objednavka.pocGra * 2500;
    }

    let newBasePrice = thisFinalPrice * objednavka.zapujcka;

    if (objednavka.doplnky === 0.05) {
      newBasePrice += newBasePrice * 0.05;
    } else if (objednavka.doplnky === 0.1) {
      newBasePrice += newBasePrice * 0.1;
    }

    setFinalPrice(newBasePrice);

    return newBasePrice;
  };

  const checkPrice = (objednavka) => {
    if (objednavka.rozpocet >= finalPrice) {
      let checkOk = 1;
      setChecked(checkOk);
    } else {
      let checkNotOk = 2;
      setChecked(checkNotOk);
    }
  };

  const gifted = (showFinalPrice) => {
    if (showFinalPrice >= 2000) {
      let giftOk = 1;
      setGift(giftOk);
    } else if (showFinalPrice < 2000) {
      let giftNotOk = 2;
      setGift(giftNotOk);
    }
  };

  return (
    <PageContainer>
      <Formular onSubmit={handleSubmit}>
        <FormSection name="nadpis">
          <MainTitle>Vaše objednávka</MainTitle>
        </FormSection>

        {/* Výběr */}
        <FormSection name="vyber">
          <SectionTitle>Výběr kola</SectionTitle>
          <InputDiv>
            <input
              type="checkbox"
              id="horske"
              onChange={(e) => {
                dispatch({
                  type: "toggle_horske",
                });
              }}
            />
            <input
              type="number"
              placeholder="počet kol"
              id="pocHor"
              value={objednavka.pocHor}
              onChange={(e) => {
                dispatch({
                  type: "update_number",
                  value: e.target.value,
                  key: "pocHor",
                });
              }}
            ></input>
            <label>Horské kolo (500 Kč/den)</label>
          </InputDiv>
          <InputDiv>
            <input
              type="checkbox"
              id="detske"
              onChange={(e) => {
                dispatch({
                  type: "toggle_detske",
                });
              }}
            />
            <input
              type="number"
              placeholder="počet kol"
              id="pocDet"
              value={objednavka.pocDet}
              onChange={(e) => {
                dispatch({
                  type: "update_number",
                  value: e.target.value,
                  key: "pocDet",
                });
              }}
            ></input>
            <label>Dětské kolo (200 Kč/den)</label>
          </InputDiv>
          <InputDiv>
            <input
              type="checkbox"
              id="silnicni"
              onChange={(e) => {
                dispatch({
                  type: "toggle_silnicni",
                });
              }}
            />
            <input
              type="number"
              placeholder="počet kol"
              id="pocSil"
              value={objednavka.pocSil}
              onChange={(e) => {
                dispatch({
                  type: "update_number",
                  value: e.target.value,
                  key: "pocSil",
                });
              }}
            ></input>
            <label>Silniční kolo (1 500 Kč/den)</label>
          </InputDiv>
          <InputDiv>
            <input
              type="checkbox"
              id="gravel"
              onChange={() => {
                dispatch({
                  type: "toggle_gravel",
                });
              }}
            />
            <input
              type="number"
              placeholder="počet kol"
              id="pocGra"
              value={objednavka.pocGra}
              onChange={(e) => {
                dispatch({
                  type: "update_number",
                  value: e.target.value,
                  key: "pocGra",
                });
              }}
            ></input>
            <label>Gravel (2 500 Kč/den)</label>
          </InputDiv>
        </FormSection>
        <FormSection name="delkaZap">
          <SectionTitle>Doba zapůjčení</SectionTitle>
          {/* Zápůjčka */}
          <label>Výběr délku zápůjčky:</label>
          <select
            id="zapujcka"
            onChange={(e) => {
              dispatch({
                type: "update_number",
                value: e.target.value,
                key: "zapujcka",
              });
            }}
          >
            <option value={0}>nevybráno</option>
            <option value={5}>5 dnů</option>
            <option value={7}>1 týden</option>
            <option value={14}>14 dnů</option>
            <option value={30}>1 měsíc (30dnů)</option>
          </select>
        </FormSection>
        <FormSection name="doplnky">
          {/* Doplňky */}
          <SectionTitle>Doplňky</SectionTitle>
          <div>
            <input
              type="radio"
              name="doplnky"
              id="dopravaFiremniKuryr"
              value={0.05}
              onChange={(e) => {
                dispatch({
                  type: "update_number",
                  value: e.target.value,
                  key: "doplnky",
                });
              }}
            />
            <label>Cyklonosič střešní (+5%)</label>
            <input
              type="radio"
              name="doplnky"
              id="dopravaCeskaPosta"
              value={0.1}
              onChange={(e) => {
                dispatch({
                  type: "update_number",
                  value: e.target.value,
                  key: "doplnky",
                });
              }}
            />
            <label>Cyklonosič na tažné zařízení (+10%)</label>
            <input
              type="radio"
              name="doplnky"
              id="dopravaOdber"
              value={0}
              onChange={(e) => {
                dispatch({
                  type: "update_number",
                  value: e.target.value,
                  key: "doplnky",
                });
              }}
            />
            <label>Není třeba cyklonosič</label>
          </div>
        </FormSection>
        <FormSection name="kalkulace">
          {/* FINALNI CENA - ZOBRAZENI + ROZPOCET */}
          <SectionTitle>Konečná kalkulace</SectionTitle>

          <label>Finální cena:</label>
          <input type="text" id="finalniCena" value="0" onChange={(e)=>setFinalPrice(newBasePrice)} disabled />
          <Potvrdit>Vypočítej cenu</Potvrdit>
          <label>Zadejte váš rozpočet:</label>
          <input
            type="text"
            id="rozpocet"
            value={objednavka.rozpocet}
            onChange={(e) => {
              dispatch({
                type: "update_number",
                value: e.target.value,
                key: "rozpocet",
              });
            }}
          />
          <KontrolaButton
            checkedPrice={checked}
            onClick={() => {
              checkPrice(objednavka);
            }}
          >
            Kontrola
          </KontrolaButton>
          <Text gifted={gift}>
            Nakoupili jste za více než 2000, a proto k objednávce obdržíte
            dárek.
          </Text>
        </FormSection>
      </Formular>
    </PageContainer>
  );
}
export default App;
