import "./App.css";
import {
  FormSection,
  Formular,
  InputDiv,
  MainTitle,
  PageContainer,
  SectionTitle,
  KontrolaButton,
  Text
} from "./AppStyles";
//
import { useReducer, useState, useEffect } from "react";
// initialState pro useReducer
const defaultObjednavka = {
  zapujcka: 0,
  doplnky: 0,
  horske: false,
  detske: false,
  silnicni: false,
  gravel: false,
  rozpocet: 0,
  email: "",
};
// reducer funkce pro useReducer
function setObjednavka(objednavka, action) {
  switch (action.type) {
    case "update_text":
      return { ...objednavka, [action.key]: action.value };
    case "update_number":
      return { ...objednavka, [action.key]: parseFloat(action.value) };
    case "toggle_bio":
      return { ...objednavka, horske: !objednavka.horske };
    case "toggle_premium":
      return { ...objednavka, detske: !objednavka.detske };
    case "toggle_chudi":
      return { ...objednavka, silnicni: !objednavka.silnicni };
    case "toggle_darek":
      return { ...objednavka, gravel: !objednavka.gravel };
    default:
      return objednavka;
  }
}

function App() {
  const [finalPrice, setFinalPrice] = useState(0);
  const [showFinalPrice, setShowFinalPrice] = useState(0);
  const [checked, setChecked] = useState();
  const [gift, setGift] = useState();
  //state objednavky spravujeme pomoci useReducer hooku
  const [objednavka, dispatch] = useReducer(setObjednavka, defaultObjednavka);

  // useEffect(() => { console.log(JSON.stringify(objednavka)) }, [objednavka]);
  useEffect(() => {
    // console.log(JSON.stringify(objednavka));
    let newFinalPrice = getFinalPrice(objednavka);
    setShowFinalPrice(newFinalPrice);
    gifted(showFinalPrice);
    console.log(gift)
    console.log(showFinalPrice)
  }, [objednavka]);

  const getFinalPrice = (objednavka) => {
    let thisBasePrice = objednavka.zapujcka * objednavka.hmotnost;
    let thisFinalPrice = thisBasePrice;
    if (objednavka.doplnky === 250) {
      thisFinalPrice += 250;
    } else if (objednavka.doplnky === 0.1) {
      thisFinalPrice += thisBasePrice * 0.1;
    }
    if (objednavka.horske) {
      thisFinalPrice += thisBasePrice * 0.3;
    }
    if (objednavka.detske) {
      thisFinalPrice += thisBasePrice * 0.5;
    }
    if (objednavka.silnicni) {
      thisFinalPrice -= thisBasePrice * 0.15;
    }
    if (objednavka.gravel) {
      thisFinalPrice += 500;
    }
    setFinalPrice(thisFinalPrice);
    return thisFinalPrice;
  };

  const checkPrice = (objednavka) => {
    if (objednavka.rozpocet >= finalPrice) {
      let checkOk = 1;
      setChecked(checkOk);
    } else {
      let checkNotOk = 2;
      setChecked(checkNotOk);
    }
  };

  const gifted = (showFinalPrice) => {
    if (showFinalPrice >= 2000) {
      let giftOk = 1;
      setGift (giftOk);
    } else if (showFinalPrice < 2000) {
      let giftNotOk = 2;
      setGift (giftNotOk);
    }
  };

  return (
    <PageContainer>
      <Formular>
        <FormSection name="nadpis">
          <MainTitle>Vaše objednávka</MainTitle>
        </FormSection>

        {/* Výběr */}
        <FormSection name="vyber">
          <SectionTitle>Výběr kola</SectionTitle>
          <InputDiv>
            <input
              type="checkbox"
              id="horske"
              onChange={(e) => {
                dispatch({
                  type: "toggle_bio",
                });
              }}
            />
            <input type="number" placeholder="počet kol"></input>
            <label>Horské kolo (500 Kč/den)</label>
          </InputDiv>
          <InputDiv>
            <input
              type="checkbox"
              id="detske"
              onChange={(e) => {
                dispatch({
                  type: "toggle_premium",
                });
              }}
            />
            <input type="number" placeholder="počet kol"></input>
            <label>Dětské kolo (200 Kč/den)</label>
          </InputDiv>
          <InputDiv>
            <input
              type="checkbox"
              id="silnicni"
              onChange={(e) => {
                dispatch({
                  type: "toggle_chudi",
                });
              }}
            />
            <input type="number" placeholder="počet kol"></input>
            <label>Silniční kolo (1 500 Kč/den)</label>
          </InputDiv>
          <InputDiv>
            <input
              type="checkbox"
              id="gravel"
              onChange={() => {
                dispatch({
                  type: "toggle_darek",
                });
              }}
            />
            <input type="number" placeholder="počet kol"></input>
            <label>Gravel (2 500 Kč/den)</label>
          </InputDiv>
        </FormSection>
        <FormSection name="delkaZap">
          <SectionTitle>Doba zapůjčení</SectionTitle>
          {/* Zápůjčka */}
          <label>Výběr délku zápůjčky:</label>
          <select
            id="zapujcka"
            onChange={(e) => {
              dispatch({
                type: "update_number",
                value: e.target.value,
                key: "zapujcka",
              });
            }}
          >
            <option value={0}>nevybráno</option>
            <option value={5}>5 dnů</option>
            <option value={7}>1 týden</option>
            <option value={14}>14 dnů</option>
            <option value={30}>1 měsíc (30dnů)</option>
          </select>
        </FormSection>
        <FormSection name="doplnky">
          {/* Doplňky */}
          <SectionTitle>Doplňky</SectionTitle>
          <div>

            <input
              type="radio"
              name="doplnky"
              id="dopravaFiremniKuryr"
              value={0.05}
              onChange={(e) => {
                dispatch({
                  type: "update_number",
                  value: e.target.value,
                  key: "doplnky",
                });
              }}
            />
            <label>Cyklonosič střešní (+5%)</label>
            <input
              type="radio"
              name="doplnky"
              id="dopravaCeskaPosta"
              value={0.10}
              onChange={(e) => {
                dispatch({
                  type: "update_number",
                  value: e.target.value,
                  key: "doplnky",
                });
              }}
            />
            <label>Cyklonosič na tažné zařízení (+10%)</label>
            <input
              type="radio"
              name="doplnky"
              id="dopravaOdber"
              value={0}
              onChange={(e) => {
                dispatch({
                  type: "update_number",
                  value: e.target.value,
                  key: "doplnky",
                });
              }}
            />
            <label>Není třeba cyklonosič</label>
          </div>
        </FormSection>
        <FormSection name="kalkulace">
          {/* FINALNI CENA - ZOBRAZENI + ROZPOCET */}
          <SectionTitle>Konečná kalkulace</SectionTitle>
          <label>Zadejte váš rozpočet:</label>
          <input
            type="text"
            id="rozpocet"
            value={objednavka.rozpocet}
            onChange={(e) => {
              dispatch({
                type: "update_number",
                value: e.target.value,
                key: "rozpocet",
              });
            }}
          />
          <label>Finální cena:</label>
          <input type="text" id="finalniCena" value={showFinalPrice} disabled />
          <KontrolaButton
            checkedPrice={checked}
            onClick={() => {
              checkPrice(objednavka);
            }}
          >
            Kontrola
          </KontrolaButton>
          <Text 
          gifted={gift}
          >Nakoupili jste za více než 2000, a proto k objednávce obdržíte dárek.
          </Text>
        </FormSection>
        <FormSection name="email">
          <SectionTitle>Kontaktní údaje</SectionTitle>
          <label>Zadejte vaši emailovou adresu</label>
          <input
            type="text"
            id="email"
            value={objednavka.email}
            onChange={(e) => {
              dispatch({
                type: "update_text",
                value: e.target.value,
                key: "email",
              });
            }}
          />
        </FormSection>
      </Formular>
    </PageContainer>
  );
}
export default App;
