import "./App.css";
import {
  FormSection,
  Formular,
  InputDiv,
  MainTitle,
  PageContainer,
  SectionTitle,
  KontrolaButton,
} from "./AppStyles";
//
import { useReducer, useState, useEffect } from "react";
// initialState pro useReducer
const defaultObjednavka = {
  destinace: "",
  pocet: 0,
  trida: 0,
  zpLet: false,
  rozpocet: 0,
  poznamka: "",
};
// reducer funkce pro useReducer
function setObjednavka(objednavka, action) {
  switch (action.type) {
    case "toggle_zpatecni":
      return { ...objednavka, zpLet: !objednavka.zpLet };
    default:
      return objednavka;
  }
}

function App() {
  const [finalPrice, setFinalPrice] = useState(0);
  const [showFinalPrice, setShowFinalPrice] = useState(0);
  const [checked, setChecked] = useState();
  //state objednavky spravujeme pomoci useReducer hooku
  const [objednavka, dispatch] = useReducer(setObjednavka, defaultObjednavka);

  // useEffect(() => { console.log(JSON.stringify(objednavka)) }, [objednavka]);
  useEffect(() => {
    // console.log(JSON.stringify(objednavka));
    let newFinalPrice = getFinalPrice(objednavka);
    setShowFinalPrice(newFinalPrice);
  }, [objednavka]);

  const getFinalPrice = (objednavka) => {
    let thisBasePrice = objednavka.destinace * objednavka.pocet;
    let thisFinalPrice = thisBasePrice;

    if (objednavka.zpLet) {
      thisFinalPrice += thisBasePrice * 0.5;
    }

    setFinalPrice(thisFinalPrice);
    return thisFinalPrice;
  };

  const checkPrice = (objednavka) => {
    if (objednavka.rozpocet >= finalPrice) {
      let checkOk = 1;
      setChecked(checkOk);
    } else {
      let checkNotOk = 2;
      setChecked(checkNotOk);
    }
  };


  return (
    <PageContainer>
      <Formular>
        <FormSection name="nadpis">
          <MainTitle>Vaše objednávka</MainTitle>
        </FormSection>
        <FormSection name="vyber">
          <SectionTitle>Destinace</SectionTitle>
          {/* VYBER destinace */}
          <label>Výběr destinace:</label>
          <select
            id="destinace"
            onChange={(e) => {
              dispatch({
                type: "update_number",
                value: e.target.value,
                key: "destinace",
              });
            }}
          >
            <option value={0}>nevybráno</option>
            <option value={500}>Praha 500 kč</option>
            <option value={3000}>Frankfurt 3 000 kč</option>
            <option value={15000}>New York 15 000 kč</option>
            <option value={30000}>Sydney 30 000 kč</option>
          </select>
          {/* HMOTNOST KRMIVA */}
          <label>Pocet letenek (1-10):</label>
          <select
            id="pocet"
            onChange={(e) => {
              dispatch({
                type: "update_number",
                value: e.target.value,
                key: "pocet",
              });
            }}
          >
            <option value={0}>nevybráno</option>
            <option value={1}>1</option>
            <option value={2}>2</option>
            <option value={3}>3</option>
            <option value={4}>4</option>
            <option value={5}>5</option>
            <option value={6}>6</option>
            <option value={7}>7</option>
            <option value={8}>8</option>
            <option value={9}>9</option>
            <option value={10}>10</option>
          </select>

        </FormSection>
        {/* Zpatecni */}
        <FormSection name="zpatecni">
          <SectionTitle>Zpáteční letenka</SectionTitle>
          <InputDiv>
            <input
              type="checkbox"
              id="zpLet"
              onChange={(e) => {
                dispatch({
                  type: "toggle_zpatecni",
                });
              }}
            />
            <label>Pokud chcete zpáteční letenku zaškrtněte pole.</label>
          </InputDiv>

        </FormSection>
        <FormSection name="trida">
          {/* Trida */}
          <SectionTitle>Třída</SectionTitle>
          <div>
            <input
              type="radio"
              name="trida"
              id="economy"
              value={0}
              onChange={(e) => {
                dispatch({
                  type: "update_number",
                  value: e.target.value,
                  key: "trida",
                });
              }}
            />
            <label>Economy class (bez přirážky)</label>
            <input
              type="radio"
              name="trida"
              id="business"
              value={0.1}
              onChange={(e) => {
                dispatch({
                  type: "update_number",
                  value: e.target.value,
                  key: "trida",
                });
              }}
            />
            <label>Business class (+25%)</label>
            <input
              type="radio"
              name="trida"
              id="royal"
              value={0.1}
              onChange={(e) => {
                dispatch({
                  type: "update_number",
                  value: e.target.value,
                  key: "trida",
                });
              }}
            />
            <label>Royal class (+50%)</label>

          </div>
        </FormSection>
        <FormSection name="kalkulace">
          {/* FINALNI CENA - ZOBRAZENI + ROZPOCET */}
          <SectionTitle>Konečná kalkulace</SectionTitle>
          <label>Zadejte váš rozpočet:</label>
          <input
            type="text"
            id="rozpocet"
            value={objednavka.rozpocet}
            onChange={(e) => {
              dispatch({
                type: "update_number",
                value: e.target.value,
                key: "rozpocet",
              });
            }}
          />
          <label>Finální cena:</label>
          <input type="text" id="finalniCena" value={showFinalPrice} disabled />
          <KontrolaButton
            checkedPrice={checked}
            onClick={() => {
              checkPrice(objednavka);
            }}
          >
            Kontrola
          </KontrolaButton>
        </FormSection>
        <FormSection name="poznamka">
          <SectionTitle>Poznámka</SectionTitle>
          <textarea
            type="text"
            id="poznamka"
            placeholder="Pokud máte specifická přání, tak nám je prosím napište do poznámky"
            value={objednavka.poznamka}
            onChange={(e) => {
              dispatch({
                type: "update_text",
                value: e.target.value,
                key: "poznamka",
              });
            }}
          />
        </FormSection>
      </Formular>
    </PageContainer>
  );
}
export default App;
